package com.example.oggym.image;

import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.util.Base64;


public  class ImageMaker  {
        // image test value
        static String imgTestValue = "data:image/jpeg;base64,";

        public static Bitmap imageDecode() {

            String cleanImage = imgTestValue.replace("data:image/png;base64,", "").replace("data:image/jpeg;base64,","");
            byte[] decodedString = Base64.decode(cleanImage,Base64.DEFAULT);
            Bitmap image = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);

            return image;
        }
}